const l=[{label:"year",seconds:31536e3},{label:"month",seconds:2592e3},{label:"day",seconds:86400},{label:"hour",seconds:3600},{label:"minute",seconds:60},{label:"second",seconds:1}];function c(e){if(!e)return"n/a ago";const n=new Date(e*1e3),t=Math.floor((Date.now()-n.getTime())/1e3),s=l.find(o=>o.seconds<t);if(!s)return null;const a=Math.floor(t/s.seconds);return`${a} ${s.label}${a!==1?"s":""} ago`}function r(e=0){return`${Math.abs(e)>999?Math.sign(e)*parseFloat((Math.abs(e)/1e3).toFixed(1))+"k":Math.sign(e)*Math.abs(e)}`}export{r as k,c as t};
