const r=[{label:"year",seconds:31536e3},{label:"month",seconds:2592e3},{label:"day",seconds:86400},{label:"hour",seconds:3600},{label:"minute",seconds:60},{label:"second",seconds:1}];function l(e){if(!e)return"n/a ago";const a=new Date(e*1e3),n=Math.floor((Date.now()-a.getTime())/1e3),t=r.find(o=>o.seconds<n);if(!t)return null;const s=Math.floor(n/t.seconds);return`${s} ${t.label}${s!==1?"s":""} ago`}function c(e=0){return`${Math.abs(e)>999?Math.sign(e)*parseFloat((Math.abs(e)/1e3).toFixed(1))+"k":Math.sign(e)*Math.abs(e)}`}function i(e){return new Date(e*1e3).toISOString().substr(14,5)}export{i as a,c as k,l as t};
